// Adjust counts for injuries and property damage

import * as turf from '@turf/turf';

// Filter collision data within the selected polygon
export const filterDataByArea = (data, polygon) => {
  return data.filter((row) => {
    const point = turf.point([row.LONGITUDE, row.LATITUDE]);
    return turf.booleanPointInPolygon(point, polygon);
  });
};

export const adjustCounts = (data) => {
    return data.map((row) => {
      let adjustedInjuries = 0;
      let adjustedProperty = 0;
  
      if (row.TYPE === 'INJURY') {
        adjustedInjuries = Math.floor(row.count * 1.78);
        adjustedProperty = row.count - (adjustedInjuries - row.count);
      } else if (row.TYPE === 'FATALITY') {
        adjustedInjuries = Math.floor(row.count * 1.017);
      } else {
        adjustedProperty = row.count;
      }
  
      return {
        ...row,
        adjustedInjuries,
        adjustedProperty,
      };
    });
  };
  
  // Calculate costs for each type of collision
  export const calculateCosts = (data) => {
    const costMatrix = {
      INJURY: { direct: 48341, human: 89408, wtp: 158654 },
      FATALITY: { direct: 225558, human: 2224580, wtp: 6707228 },
      PROPERTY: { direct: 14065, human: 0, wtp: 0 },
    };
  
    return data.map((row) => {
      const { TYPE, adjustedInjuries, adjustedProperty } = row;
      const costs = costMatrix[TYPE];
  
      let totalDirect = 0;
      let totalHuman = 0;
      let totalWtp = 0;
  
      if (TYPE === 'INJURY') {
        totalDirect = adjustedInjuries * costs.direct;
        totalHuman = adjustedInjuries * costs.human;
        totalWtp = adjustedInjuries * costs.wtp;
      } else if (TYPE === 'FATALITY') {
        totalDirect = adjustedInjuries * costs.direct;
        totalHuman = adjustedInjuries * costs.human;
        totalWtp = adjustedInjuries * costs.wtp;
      } else if (TYPE === 'PROPERTY') {
        totalDirect = adjustedProperty * costs.direct;
      }
  
      const total = totalDirect + totalHuman + totalWtp;
      const inflationAdjusted = total * 1.2084;
  
      return {
        ...row,
        totalDirect,
        totalHuman,
        totalWtp,
        total,
        inflationAdjusted,
      };
    });
  };
  
  // Aggregate totals and calculate yearly/daily averages
  export const aggregateTotals = (data) => {
    const totals = data.reduce(
      (acc, row) => ({
        totalDirect: acc.totalDirect + row.totalDirect,
        totalHuman: acc.totalHuman + row.totalHuman,
        totalWtp: acc.totalWtp + row.totalWtp,
        total: acc.total + row.total,
        inflationAdjusted: acc.inflationAdjusted + row.inflationAdjusted,
      }),
      { totalDirect: 0, totalHuman: 0, totalWtp: 0, total: 0, inflationAdjusted: 0 }
    );
  
    const yearly = {
      totalDirect: totals.totalDirect / 3,
      totalHuman: totals.totalHuman / 3,
      totalWtp: totals.totalWtp / 3,
      total: totals.total / 3,
      inflationAdjusted: totals.inflationAdjusted / 3,
    };
  
    const daily = {
      totalDirect: yearly.totalDirect / 365,
      totalHuman: yearly.totalHuman / 365,
      totalWtp: yearly.totalWtp / 365,
      total: yearly.total / 365,
      inflationAdjusted: yearly.inflationAdjusted / 365,
    };
  
    return { totals, yearly, daily };
  };
