import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import MapComponent from './MapComponent';

export default function Home() {
  const [results, setResults] = useState(null);
  const [collisionData, setCollisionData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isChrome, setIsChrome] = useState(true);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const userAgent = navigator.userAgent.toLowerCase();
    setIsChrome(
      userAgent.includes('chrome') &&
      !userAgent.includes('edge') &&
      !userAgent.includes('opr')
    );
    setIsMobile(window.innerWidth < 1024);
  }, []);

  useEffect(() => {
    fetch('/collision-data.csv')
      .then((response) => response.text())
      .then((csvText) => {
        Papa.parse(csvText, {
          header: true,
          dynamicTyping: true,
          complete: (result) => {
            if (result.errors.length > 0) {
              console.error('Errors parsing CSV:', result.errors);
            } else {
              setCollisionData(result.data);
            }
            setLoading(false);
          },
        });
      })
      .catch((error) => {
        console.error('Error loading collision data:', error);
        setLoading(false);
      });
  }, []);

  return (
    <div>
      <div className="w-full bg-olive pt-32 py-6 shadow-sm">
        <div className="max-w-7xl mx-auto px-4 text-center">
          <h1 className="font-playfair text-2xl md:text-5xl text-cream">
            Click and drag to select an area and see how much collisions cost Saskatoon
          </h1>
        </div>
      </div>
      <div className="max-w-7xl mx-auto p-4 md:p-8">
        <div className="flex flex-col lg:flex-row gap-8">
          <div className="flex-1 h-[600px] lg:h-[800px]">
            {!isChrome ? (
              <p className="text-red-600 font-semibold">This application works best in Google Chrome. Please switch to Chrome for the best experience.</p>
            ) : isMobile ? (
              <p className="text-red-600 font-semibold">This application is not optimized for mobile. Please use a desktop for the best experience.</p>
            ) : loading ? (
              <p>Loading collision data...</p>
            ) : (
              <MapComponent
                collisionData={collisionData}
                onResultsUpdate={setResults}
              />
            )}
          </div>
          <div className="w-full lg:w-1/3 bg-white p-6 rounded-lg shadow-md">
            <h2 className="font-playfair text-2xl text-olive mb-4">Cost Analysis</h2>
            <div className="space-y-4">
              {results ? (
                <>
                  <div>
                    <h3 className="font-semibold text-gray-700">Cost of Collisions Per Year</h3>
                    <ul>
                      {Object.entries(results.yearly).map(([year, cost]) => (
                        <li key={year}>{year}: ${cost.toLocaleString()}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <h3 className="font-semibold text-gray-700">Cost of Collisions Per Day</h3>
                    <p>${results.daily.toLocaleString()}</p>
                  </div>
                </>
              ) : (
                <p>Select an area to see the cost analysis.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
