import React from 'react';
import { FaInstagram } from 'react-icons/fa';

const Footer = () => {
  return (
    <footer className="w-full bg-charcoal">
      <div className="max-w-7xl mx-auto px-8 py-4">
        <div className="flex flex-col md:flex-row justify-center items-center space-y-2 md:space-y-0 md:space-x-4">
          {/* Combined block for text, email, and Instagram link */}
          <p className="font-montserrat text-sm text-cream">
            Website by Nick Pollett •{' '}
            <a
              href="https://github.com/nickpollett/portfolio"
              className="hover:text-muted-green transition-colors duration-300"
            >
               Github 
            </a> •{' '}
            <a
              href="mailto:nickpollettphoto@gmail.com"
              className="hover:text-muted-green transition-colors duration-300"
            >
              nicholaspollett@hotmail.com
            </a>{' '}
            •{' '}
            <a
              href="https://www.instagram.com/nickpollett/"
              className="hover:text-muted-green transition-colors duration-300"
              aria-label="Instagram"
            >
              <FaInstagram className="inline-block w-4 h-4 align-middle" />
            </a>
          </p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
