import { NavLink } from 'react-router-dom';

const NavLinkStyled = ({ to, children }) => (
  <NavLink
    to={to}
    className={({ isActive }) =>
      `text-2xl font-playfair font-medium ${
        isActive ? 'text-cream' : 'text-cream'
      } transition-all duration-300 hover:text-muted-green no-underline`
    }
  >
    {children}
  </NavLink>
);

export default function NavigationBar({ isMobile }) {
  return (
    <nav className="w-full bg-charcoal fixed top-0 left-0 z-50 shadow-lg">
      <div className="max-w-6xl mx-auto px-4">
        {isMobile ? (
          // Mobile layout: Just the logo
          <div className="flex items-center justify-center h-28">
            <NavLink to="/">
              <img
                src={'/images/Collision.png'}
                alt="Logo"
                className="h-24 w-auto object-cover object-center"
              />
            </NavLink>
          </div>
        ) : (
          // Desktop layout: Logo and navigation links
          <div className="hidden md:flex items-center justify-between h-28">
            <div className="flex-shrink-0 hover:opacity-80 transition-opacity duration-300">
              <NavLink to="/">
                <img
                  src={'/images/Collision.png'}
                  alt="Logo"
                  className="h-24 w-auto object-cover object-center"
                />
              </NavLink>
            </div>
            <div className="flex bg-olive space-x-10 rounded-lg px-4 py-2">
              <NavLinkStyled to="/about">About The Calculations</NavLinkStyled>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
}
