import React, { useState, useRef, useCallback } from 'react';
import ReactMapGL, { Source, Layer } from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import { calculateCosts, aggregateTotals, filterDataByArea, adjustCounts } from '../utils/costCalculations';

const MapComponent = ({ collisionData, onResultsUpdate }) => {
  const [viewport, setViewport] = useState({
    latitude: 52.1332,
    longitude: -106.6700,
    zoom: 11,
    width: '100%',
    height: '100%',
  });

  const [firstCorner, setFirstCorner] = useState(null);
  const [secondCorner, setSecondCorner] = useState(null);
  const [selectedArea, setSelectedArea] = useState(null);
  const mapRef = useRef(null);

  // Add debug logging to viewport changes
  const handleViewportChange = useCallback((newViewport) => {
    console.log('Viewport changed:', newViewport);
    setViewport(newViewport);
  }, []);

  const handleFirstClick = useCallback((event) => {
    console.log('First click:', event.lngLat);
    const { lngLat } = event;
    setFirstCorner(lngLat);
    setSecondCorner(null);
    setSelectedArea(null);
  }, []);

  const handleSecondClick = useCallback(
    (event) => {
      if (!firstCorner) return;

      console.log('Second click:', event.lngLat);
      const { lngLat } = event;
      setSecondCorner(lngLat);

      const coordinates = [
        [
          [firstCorner.lng, firstCorner.lat],
          [firstCorner.lng, lngLat.lat],
          [lngLat.lng, lngLat.lat],
          [lngLat.lng, firstCorner.lat],
          [firstCorner.lng, firstCorner.lat],
        ],
      ];

      setSelectedArea({
        type: 'Polygon',
        coordinates,
      });

      const filteredData = filterDataByArea(collisionData, {
        type: 'Polygon',
        coordinates,
      });

      const counts = {
        INJURY: filteredData.filter((row) => row.TYPE === 'INJURY').length,
        FATALITY: filteredData.filter((row) => row.TYPE === 'FATALITY').length,
        PROPERTY: filteredData.filter((row) => row.TYPE === 'PROPERTY').length,
      };

      const formattedData = [
        { TYPE: 'INJURY', count: counts.INJURY },
        { TYPE: 'FATALITY', count: counts.FATALITY },
        { TYPE: 'PROPERTY', count: counts.PROPERTY },
      ];

      const adjustedData = adjustCounts(formattedData);
      const costData = calculateCosts(adjustedData);
      const { totals, yearly, daily } = aggregateTotals(costData);

      onResultsUpdate({ totals, yearly, daily });
      setFirstCorner(null);
    },
    [firstCorner, collisionData, onResultsUpdate]
  );

  const handleMapClick = useCallback(
    (event) => {
      if (!firstCorner) {
        handleFirstClick(event);
      } else {
        handleSecondClick(event);
      }
    },
    [firstCorner, handleFirstClick, handleSecondClick]
  );

  return (
    <div style={{ width: '100%', height: '100%', position: 'relative' }}>
      <ReactMapGL
        {...viewport}
        ref={mapRef}
        mapboxAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
        onViewportChange={handleViewportChange}
        onClick={handleMapClick}
        mapStyle="mapbox://styles/mapbox/streets-v11"
        scrollZoom={true}
        doubleClickZoom={true}
        dragPan={true}
        dragRotate={true}
        touchZoom={true}
        touchRotate={true}
        minZoom={1}
        maxZoom={20}
      >
        {selectedArea && (
          <Source type="geojson" data={{ type: 'Feature', geometry: selectedArea }}>
            <Layer
              id="selected-area"
              type="fill"
              paint={{
                'fill-color': '#3FB1CE',
                'fill-opacity': 0.5,
              }}
            />
            <Layer
              id="selected-area-outline"
              type="line"
              paint={{
                'line-color': '#3FB1CE',
                'line-width': 2,
              }}
            />
          </Source>
        )}
      </ReactMapGL>
    </div>
  );
};

export default MapComponent;
